example.code:
// Create a CIImage from a file.
CIImage flower = CIImage.FromCGImage (UIImage.FromFile ("flower.png").CGImage);

// Construct the WhitePointAdjust filter
var whitePointAdjust = new CIWhitePointAdjust()
{
	Image = flower,
	Color = new CIColor(new CGColor(255F, 0, 187F)) // A Magenta Color
};

// Get the adjusted image from the filter.
var output = whitePointAdjust.OutputImage;

// To render the results, we need to create a context, and then
// use one of the context rendering APIs, in this case, we render the
// result into a CoreGraphics image, which is merely a useful representation
//
var context = CIContext.FromOptions (null);

var cgimage = context.CreateCGImage (output, output.Extent);

// The above cgimage can be added to a screen view, for example, this
// would add it to a UIImageView on the screen:
myImageView.Image = UIImage.FromImage (cgimage);
